// App.js
import React, { useState } from 'react';
import BookmarkButton from './BookmarkButton';
import FavoriteList from './FavoriteList';

const App = () => {
  const [stories, setStories] = useState([
    {
      id: '1',
      title: 'Example Story 1',
      content: 'This is the content of example story 1.'
    },
    {
      id:  '2',
      title: 'Example Story 2',
      content: 'This is the content of example story 2.'
    },
    {
      id: '3',
      title: 'Example Story 3',
      content: 'This is the content of example story 3.'
    },
    {
      id: '4',
      title: 'Example Story 4',
      content: 'This is the content of example story 4.'
    }
  ]);
  const [favoritesChanged, setFavoritesChanged] = useState(false);
  const [favorites, setFavorites] = useState([]);

  // Callback function để cập nhật trạng thái thay đổi yêu thích
  const handleFavoritesChanged = (storyId, isFavorite) => {
    if (isFavorite) {
      const story = stories.find(story => story.id === storyId);
      setFavorites([...favorites, story]);
    } else {
      const updatedFavorites = favorites.filter(item => item.id !== storyId);
      setFavorites(updatedFavorites);
    }
    setFavoritesChanged(!favoritesChanged); // Đảo ngược giá trị để trigger re-render
  };

  const handleRemoveStory = (id) => {
    const updatedStories = stories.filter(story => story.id !== id);
    setStories(updatedStories);

    // Kiểm tra xem câu chuyện vừa xóa có trong danh sách yêu thích hay không
    const isFavorite = favorites.some(item => item.id === id);
    if (isFavorite) {
      const updatedFavorites = favorites.filter(item => item.id !== id);
      setFavorites(updatedFavorites);
      setFavoritesChanged(!favoritesChanged); // Trigger re-render của FavoriteList
    }
  };

  return (
    <div>
      <h2>Story List</h2>
      <ul>
        {stories.map(story => (
          <li key={story.id}>
            {story.title}
            <BookmarkButton story={story} onToggleFavorite={handleFavoritesChanged} />
            <button onClick={() => handleRemoveStory(story.id)}>Remove Story</button>
          </li>
        ))}
      </ul>
      <hr />
      <FavoriteList key={favoritesChanged.toString()} favorites={favorites} />
    </div>
  );
};

export default App;
