import React, { useState, useEffect } from 'react';

const BookmarkButton = ({ storyId }) => {
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [showBookmarkMessage, setShowBookmarkMessage] = useState(false); // State để quản lý hiển thị message

  useEffect(() => {
    // Kiểm tra local storage để xem truyện đã được bookmark chưa
    const bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    if (bookmarks.includes(storyId)) {
      setIsBookmarked(true);
    }
  }, [storyId]);

  const handleBookmark = () => {
    let bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    if (isBookmarked) {
      // Nếu truyện đã được bookmark, remove nó
      bookmarks = bookmarks.filter(id => id !== storyId);
      setIsBookmarked(false);
      setShowBookmarkMessage(false); // Ẩn message khi remove bookmark
    } else {
      // Nếu truyện chưa được bookmark, add nó
      bookmarks.push(storyId);
      setIsBookmarked(true);
      setShowBookmarkMessage(true); // Hiển thị message khi add bookmark
    }
    localStorage.setItem('bookmarks', JSON.stringify(bookmarks));
  };

  return (
    <div>
      <button onClick={handleBookmark}>
        {isBookmarked ? 'Remove Bookmark' : 'Add Bookmark'}
      </button>
    </div>
  );
};

export default BookmarkButton;
