// App.js
import React, { useState, useEffect } from 'react';
import BookmarkButton from './BookmarkButton';
import FavoriteList from './FavoriteList';

const App = () => {
  const [stories, setStories] = useState([
    {
      id: '1',
      title: 'Example Story 1',
      content: 'This is the content of example story 1.'
    },
    {
      id: '2',
      title: 'Example Story 2',
      content: 'This is the content of example story 2.'
    },
    {
      id: '3',
      title: 'Example Story 3',
      content: 'This is the content of example story 3.'
    },
    {
      id: '4',
      title: 'Example Story 4',
      content: 'This is the content of example story 4.'
    }
  ]);

  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Lấy dữ liệu từ localStorage vào ban đầu
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(storedFavorites);

    // Cập nhật trạng thái yêu thích của các câu chuyện
    const updatedStories = stories.map(story => ({
      ...story,
      isFavorite: storedFavorites.some(favorite => favorite.id === story.id)
    }));
    setStories(updatedStories);
  }, [stories]);

  const handleToggleFavorite = (id, isCurrentlyFavorite) => {
    let updatedFavorites;
    if (isCurrentlyFavorite) {
      updatedFavorites = favorites.filter(item => item.id !== id);
    } else {
      updatedFavorites = [...favorites, { id: id, title: getStoryById(id).title }];
    }
    setFavorites(updatedFavorites);
  };

  const handleRemoveStory = (id) => {
    const updatedStories = stories.filter(story => story.id !== id);
    setStories(updatedStories);
  };

  const getStoryById = (id) => {
    return stories.find(story => story.id === id);
  };

  return (
    <div>
      <h2>Story List</h2>
      <ul>
        {stories.map(story => (
          <li key={story.id}>
            {story.title}
            <BookmarkButton
              story={story}
              isFavorite={story.isFavorite}
              onToggleFavorite={handleToggleFavorite}
            />
            <button onClick={() => handleRemoveStory(story.id)}>Remove Story</button>
          </li>
        ))}
      </ul>
      <hr />
      <FavoriteList
        favorites={favorites}
        onRemoveFavorite={(id) => {
          handleToggleFavorite(id, true); // Xóa yêu thích khi click Remove Favorite
        }}
      />
    </div>
  );
};

export default App;
