// FavoriteList.js
import React, { useEffect } from 'react';

const FavoriteList = ({ favorites, onRemoveFavorite }) => {
  const handleRemoveFavorite = (id) => {
    const updatedFavorites = favorites.filter(item => item.id !== id);
    onRemoveFavorite(updatedFavorites); // Thông báo cho App component về sự thay đổi yêu thích
  };

  useEffect(() => {
    // Cập nhật localStorage khi favorites thay đổi
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  return (
    <div>
      <h2>Favorite Stories</h2>
      <ul>
        {favorites.map(item => (
          <li key={item.id}>
            {item.title}{' '}
            <button onClick={() => handleRemoveFavorite(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FavoriteList;
