import React, { useState, useEffect } from 'react';

const BookmarkButton = ({ story }) => {
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    // Kiểm tra xem story đã được bookmark hoặc favorite chưa

    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setIsFavorite(favorites.some(item => item.id === story.id));
  }, [story]);


  const handleFavorite = () => {
    let favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    if (isFavorite) {
      favorites = favorites.filter(item => item.id !== story.id);
      setIsFavorite(false);
    } else {
      favorites.push({ id: story.id, title: story.title });
      setIsFavorite(true);
    }

    localStorage.setItem('favorites', JSON.stringify(favorites));
  };

  return (
    <div>
      <button onClick={handleBookmark}>
        {isBookmarked ? 'Remove Bookmark' : 'Add Bookmark'}
      </button>
      <button onClick={handleFavorite}>
        {isFavorite ? 'Remove Favorite' : 'Add Favorite'}
      </button>
    </div>
  );
};

export default BookmarkButton;
