import React, { useState, useEffect } from 'react';

const BookmarkButton = ({ storyId }) => {
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    // Kiểm tra xem storyId đã được bookmark hoặc favorite chưa
    const bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    setIsBookmarked(bookmarks.some(item => item.storyId === storyId));
    setIsFavorite(favorites.some(item => item.storyId === storyId));
  }, [storyId]);

  const handleBookmark = () => {
    let bookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];

    if (isBookmarked) {
      bookmarks = bookmarks.filter(item => item.storyId !== storyId);
      setIsBookmarked(false);
    } else {
      bookmarks.push({ storyId });
      setIsBookmarked(true);
    }

    localStorage.setItem('bookmarks', JSON.stringify(bookmarks));
  };

  const handleFavorite = () => {
    let favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    if (isFavorite) {
      favorites = favorites.filter(item => item.storyId !== storyId);
      setIsFavorite(false);
    } else {
      favorites.push({ storyId });
      setIsFavorite(true);
    }

    localStorage.setItem('favorites', JSON.stringify(favorites));
  };

  return (
    <div>
      <button onClick={handleBookmark}>
        {isBookmarked ? 'Remove Bookmark' : 'Add Bookmark'}
      </button>
      <button onClick={handleFavorite}>
        {isFavorite ? 'Remove Favorite' : 'Add Favorite'}
      </button>
    </div>
  );
};

export default BookmarkButton;
