import React, { useState, useEffect } from 'react';

const BookmarkButton = ({ story, onToggleFavorite, isFavorite }) => {
  const [localIsFavorite, setLocalIsFavorite] = useState(false);

  useEffect(() => {
    setLocalIsFavorite(isFavorite);
  }, [isFavorite]);

  const handleFavorite = () => {
    let favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    if (localIsFavorite) {
      favorites = favorites.filter(item => item.id !== story.id);
      setLocalIsFavorite(false);
    } else {
      favorites.push({ id: story.id, title: story.title });
      setLocalIsFavorite(true);
    }

    localStorage.setItem('favorites', JSON.stringify(favorites));
    onToggleFavorite(); // Thông báo cho App component biết đã thay đổi yêu thích
  };

  return (
    <button onClick={handleFavorite}>
      {localIsFavorite ? 'Xóa khỏi Yêu thích' : 'Thêm vào Yêu thích'}
    </button>
  );
};

export default BookmarkButton;
