// App.js
import React, { useState } from 'react';
import BookmarkButton from './BookmarkButton';
import FavoriteList from './FavoriteList';

const App = () => {
  const [stories, setStories] = useState([
    {
      id: '1',
      title: 'Example Story 1',
      content: 'This is the content of example story 1.'
    },
    {
      id:  '2',
      title: 'Example Story 2',
      content: 'This is the content of example story 2.'
    },
    {
      id: '3',
      title: 'Example Story 3',
      content: 'This is the content of example story 3.'
    },
    {
      id: '4',
      title: 'Example Story 4',
      content: 'This is the content of example story 4.'
    }
  ]);
  const [favorites, setFavorites] = useState([]);

  // Thêm một hàm để cập nhật trạng thái yêu thích từ FavoriteList
  const handleToggleFavorite = (updatedFavorites) => {
    setFavorites(updatedFavorites);

    // Kiểm tra và cập nhật trạng thái yêu thích của từng câu chuyện
    const updatedStories = stories.map(story => {
      if (updatedFavorites.some(item => item.id === story.id)) {
        return { ...story, isFavorite: true };
      } else {
        return { ...story, isFavorite: false };
      }
    });

    setStories(updatedStories);
  };

  const handleRemoveStory = (id) => {
    const updatedStories = stories.filter(story => story.id !== id);
    setStories(updatedStories);
  };

  return (
    <div>
      <h2>Story List</h2>
      <ul>
        {stories.map(story => (
          <li key={story.id}>
            {story.title}
            <BookmarkButton
              story={story}
              favorites={favorites}
              onToggleFavorite={handleToggleFavorite}
            />
            <button onClick={() => handleRemoveStory(story.id)}>Remove Story</button>
          </li>
        ))}
      </ul>
      <hr />
      <FavoriteList favorites={favorites} onRemoveFavorite={handleToggleFavorite} />
    </div>
  );
};

export default App;
