import React, { useState } from 'react';
import BookmarkButton from './BookmarkButton';
import FavoriteList from './FavoriteList';

const App = () => {
  const [stories] = useState([
    {
      id: '1',
      title: 'Truyện Mẫu 1',
      content: 'Nội dung của truyện mẫu 1.'
    },
    {
      id: '2',
      title: 'Truyện Mẫu 2',
      content: 'Nội dung của truyện mẫu 2.'
    },
    {
      id: '3',
      title: 'Truyện Mẫu 3',
      content: 'Nội dung của truyện mẫu 3.'
    },
    {
      id: '4',
      title: 'Truyện Mẫu 4',
      content: 'Nội dung của truyện mẫu 4.'
    }
  ]);

  const handleToggleFavorite = () => {
    // Có thể xử lý gì đó ở đây nếu cần thiết
  };

  return (
    <div>
      <h2>Danh sách Truyện</h2>
      <ul>
        {stories.map(story => (
          <li key={story.id}>
            {story.title}
            <BookmarkButton
              story={story}
              onToggleFavorite={handleToggleFavorite}
            />
          </li>
        ))}
      </ul>
      <hr />
      <FavoriteList onUpdateFavorite={handleToggleFavorite} />
    </div>
  );
};

export default App;
