import React, { useState, useEffect } from 'react';

const FavoriteList = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(storedFavorites);
  }, []);

  useEffect(() => {
    // Cập nhật localStorage c  hỉ khi favorites thay đổi
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const handleRemoveFavorite = (id) => {
    // Cập nhật state favorites bằng cách lọc ra các phần tử khác với id được chọn
    const updatedFavorites = favorites.filter(item => item.id !== id);
    
    // Cập nhật state mới
    setFavorites(updatedFavorites);
  };

  return (
    <div>
      <h2>Favorite Stories</h2>
      <ul>
        {favorites.map(item => (
          <li key={item.id}>
            {item.title}{' '}
            <button onClick={() => handleRemoveFavorite(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FavoriteList;
