// BookmarkButton.js
import React, { useState, useEffect } from 'react';

const BookmarkButton = ({ story, favorites, onToggleFavorite }) => {
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    // Kiểm tra xem story đã được bookmark hoặc favorite chưa
    setIsFavorite(favorites.some(item => item.id === story.id));
  }, [favorites, story]);

  const handleFavorite = () => {
    let updatedFavorites = [...favorites];

    if (isFavorite) {
      updatedFavorites = updatedFavorites.filter(item => item.id !== story.id);
    } else {
      updatedFavorites.push({ id: story.id, title: story.title });
    }

    onToggleFavorite(updatedFavorites);
  };

  return (
    <button onClick={handleFavorite}>
      {isFavorite ? 'Remove Favorite' : 'Add Favorite'}
    </button>
  );
};

export default BookmarkButton;
